INSERT INTO [dbo].[Drinks] ([idDrink], [strDrink], [strTags], [strCategory], [strIBA], [strAlcoholic], [strGlass], [strInstructions], [strDrinkThumb], [strIngredient1], [strIngredient2], [strIngredient3], [strIngredient4], [strIngredient5], [strIngredient6], [strIngredient7], [strIngredient8], [strIngredient9], [strIngredient10], [strIngredient11], [strIngredient12], [strIngredient13], [strIngredient14], [strIngredient15], [strMeasure1], [strMeasure2], [strMeasure3], [strMeasure4], [strMeasure5], [strMeasure6], [strMeasure7], [strMeasure8], [strMeasure9], [strMeasure10], [strMeasure11], [strMeasure12], [strMeasure13], [strMeasure14], [strMeasure15], [dateModified]) VALUES (12792, N'Amaretto Liqueur', NULL, N'Homemade Liqueur', NULL, N'Alcoholic', N'Collins Glass', N'Combine sugar and 3/4 cup water in a small saucepan. Bring to a boil, stirring constantly. Reduce heat and simmer until all sugar is dissolved. Remove from heat and cool. In an aging container, combine apricot halves, almond extract, grain alcohol with 1/2 cup water, and brandy. Stir in cooled sugar syrup mixture. Cap and let age for 2 days. Remove apricot halves. (Save apricot halves, can be used for cooking). Add food coloring and glycerine. Stir, recap and continue aging for 1 to 2 months. Re-bottle as desired. Liqueur is ready to serve but will continue to improve with additional aging.', N'https://www.thecocktaildb.com/images/media/drink/swqxuv1472719649.jpg', N'Sugar', N'Water', N'Apricot', N'Almond flavoring', N'Grain alcohol', N'Water', N'Brandy', N'Food coloring', N'Food coloring', N'Food coloring', N'Glycerine', NULL, NULL, NULL, NULL, N'1 cup', N'3/4 cup', N'2', N'1 tblsp', N'1/2 cup pure', N'1/2 cup', N'1 cup', N'3 drops yellow', N'6 drops red', N'2 drops blue', N'1/2 tsp', NULL, NULL, NULL, NULL, N'2016-09-01 09:47:29')
